package LOOP1;

public class sumofNnumbeers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		i=1;//condition
		int sum =0;
		
		while (i<=10)//condition
		{
		sum=sum+i;
			i++;//i=i+1
			{
			System.out.println("sum of n numbers: "+sum);
		}
			

	}

	}}
