
public class geomatrey {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double n, res1, res2, res3;
		n = 90;
		double pi = 3.14;
		double r = n * pi / 180;
		res1 = Math.sin(r);
		res2 = Math.cos(r);
		res3 = Math.tan(r);
		System.out.println("sine:" + res1);
		System.out.println("cos:" + res2);
		System.out.println("tan:" + res3);

	}

}
